import React from 'react';
import BannerPic from '../../assets/images/pexels-sergei-starostin-6636476 (1).jpg';
import PrimaryButton from '../Shared/PrimaryButton';

const Banner = () => {
    return (
        <div className="hero min-h-screen">
            <div className="hero-content flex-col lg:flex-row-reverse">
                <img src={BannerPic} className="max-w-sm rounded-lg shadow-2xl" />
                <div>
                    <h1 className="text-5xl font-bold">Your New Smile Starts Here</h1>
                    <p className="py-6"> We specialize in Point of Sale systems for retail stores. For any POS system, we do offer complete bundles or individual POS equipment components, as well as a variety of POS Software for any restaurant, retail or business need. With any of our complete POS systems, we provide full original manufacturer warranty, quality brand new POS equipment, and 30 days free phone support on the POS software.</p>
                    <PrimaryButton>Get Started</PrimaryButton>
                </div>
            </div>
        </div>
    );
};

export default Banner;
